import asyncio
import inspect
import sys
import time

import requests
from config import *
from web3 import Web3


def check_holders_1k(token_address):
    url = f"https://api.covalenthq.com/v1/56/tokens/{token_address}/token_holders/?format=JSON&page-size=1000&key=ckey_cfa104fa7b3847e89f1ab12df46"

    res = requests.get(url).json()
    # print(res)
    try:
        if int(res['error_code']) == 406:
            print("Too many Holders")
            return True
    except:
        pass
    holders = res['data']['items']
    print(len(holders))

    return len(holders) == 1000


def handle_event(event):
    try:
        # remove the quotes in the transaction hash
        transaction = Web3.toJSON(event).strip('"')
        # use the transaction hash (that we removed the '"' from to get the details of the transaction
        transaction = web3.eth.get_transaction(transaction)
        # print(transaction)
        # set the variable to the "to" address in the message
        to = transaction['to']
        # if the to address in the message is the router
        if to == router and transaction['from'] == web3.toChecksumAddress(address_to_track):
            # print the transaction and its details
            print(transaction)
            data = contract.decode_function_input(transaction['input'])
            print(data)
            if 'swap' in str(data[0]):
                check_success = check_holders_1k(data[1]['path'][0])
                if check_success:
                    swap_tokenv2(transaction, data)

        else:
            print('Not what we are looking for')
    except Exception as err:
        # print transactions with errors. Expect to see transactions people submitted with errors
        exc_type, exc_obj, exc_tb = sys.exc_info()
        print(f'error: {err}: ', exc_tb.tb_lineno)


async def log_loop(event_filter, poll_interval):
    while True:
        for event in event_filter.get_new_entries():
            handle_event(event)
        await asyncio.sleep(poll_interval)


